  `theme` varchar(255) DEFAULT NULL COMMENT '主题',
  `destination` varchar(255) DEFAULT NULL COMMENT '目的地',
  `groups` varchar(200) NOT NULL,
  `tour_map` varchar(255) DEFAULT NULL,
  `people` varchar(50) DEFAULT NULL COMMENT '活动人数（公司出游）',
  `company_intro` varchar(255) DEFAULT NULL COMMENT '公司活动简介',
  PRIMARY KEY (`Id`)
) ENGINE=InnoDB AUTO_INCREMENT=32 DEFAULT CHARSET=utf8 COMMENT='线路';

