INSERT INTO `images` VALUES ('133', 'uploads/images/1341978677_oo_I_o_.jpg', '33.82', 'image/jpeg', '四姑娘山', '川西  旅游  四姑娘山  ', '四姑娘山位于阿坝藏族羌族自治州，由四座长年被冰雪覆盖的山峰组成。如同头披白纱，姿容俊俏的四位少女。其中幺妹身材苗条、体态婀娜，常说的“四姑娘”就是指这座最高最美的雪峰。', 'uploads/middels/1341978677_oo_I_o_.jpg', 'uploads/thumbnails/1341978677_oo_I_o_.jpg');
INSERT INTO `images` VALUES ('134', 'uploads/images/1341978798DSC_0256.jpg', '37.71', 'image/jpeg', '峨眉山', '川西  旅游  峨眉山 ', '峨眉山位于中国四川峨眉山市境内，景区面积154平方公里，最高峰万佛顶海拔3099米。地势陡峭，风景秀丽，有“秀甲天下”之美誉。', 'uploads/middels/1341978798DSC_0256.jpg', 'uploads/thumbnails/1341978798DSC_0256.jpg');
INSERT INTO `images` VALUES ('135', 'uploads/images/1341978799DSC_0212.jpg', '37.37', 'image/jpeg', '乐山 ', '川西  旅游  乐山', '乐山风光秀丽，物产富庶，是世界著名的生态和文化旅游胜地、中国旅游版图的重镇、中国最佳旅游目的地和中国世界自然与文化遗产城市之一。', 'uploads/middels/1341978799DSC_0212.jpg', 'uploads/thumbnails/1341978799DSC_0212.jpg');
