INSERT INTO `uto_images` VALUES ('133', 'uploads/images/1341978677_oo_I_o_.jpg', '33.82', 'image/jpeg', '四姑娘山', '川西  旅游  四姑娘山  ', '四姑娘山位于阿坝藏族羌族自治州，由四座长年被冰雪覆盖的山峰组成。如同头披白纱，姿容俊俏的四位少女。其中幺妹身材苗条、体态婀娜，常说的“四姑娘”就是指这座最高最美的雪峰。', 'uploads/middels/1341978677_oo_I_o_.jpg', 'uploads/thumbnails/1341978677_oo_I_o_.jpg');
