INSERT INTO `uto_images` VALUES ('135', 'uploads/images/1341978799DSC_0212.jpg', '37.37', 'image/jpeg', '乐山 ', '川西  旅游  乐山', '乐山风光秀丽，物产富庶，是世界著名的生态和文化旅游胜地、中国旅游版图的重镇、中国最佳旅游目的地和中国世界自然与文化遗产城市之一。', 'uploads/middels/1341978799DSC_0212.jpg', 'uploads/thumbnails/1341978799DSC_0212.jpg');
