INSERT INTO `uto_web_page` VALUES ('1', 'index', '友途旅行网 专注四川轻户外体验式旅游', '友途旅行网 四川旅游 川西旅游 四川体验式旅游 轻户外旅游', '友途旅行网，扎根四川，专注轻户外体验式旅游。独家包装的旅游线路，纯玩不购物，让游客体验到真正的旅游，给您设计有意义的旅行。', '');
